mapper.select:
SELECT p.title AS title,p.cseq , SUM(r.totalprice) AS totalprice
FROM Product p LEFT JOIN Reservation r ON p.cseq = r.cseq
WHERE p.id = #{param1} and (r.payment='N' or r.payment='') and endedclass='Y' GROUP BY p.cseq;

mapper.update:
update product set price=#{price},  title=#{title}, content=#{content},zip_num=#{zip_num},
                   address1=#{address1},address2=#{address2},address3=#{address3},category=#{category},
                   image=#{image},savefilename=#{savefilename},max_people=#{max_people}
               where cseq=#{cseq}
