mapper.update:
UPDATE reservation
SET payment = #{status}
WHERE reseq = #{reseq}

mapper.select:
SELECT r.reseq, r.userid, p.title, r.people, r.classday, r.classtime, r.payment, r.endedclass FROM reservation r LEFT JOIN product p ON r.cseq = p.cseq
AND (r.userid LIKE CONCAT('%', #{searchQuery}, '%') OR p.title LIKE CONCAT('%', #{searchQuery}, '%'))
AND r.payment = #{status}
AND NOT r.payment = ''
ORDER BY r.reseq DESC LIMIT #{startNum}, #{displayRow}
